import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { List } from '../list';
import { ListService } from '../list.service';

@Component({
  selector: 'app-get-all',
  templateUrl: './get-all.component.html',
  styleUrls: ['./get-all.component.css']
})
export class GetAllComponent implements OnInit{



  constructor (private rou:Router,private ser:ListService) {}

list:List[]=[];

ngOnInit(): void {
this.getAll();
  }

  getAll() {
    this.ser.getAll().subscribe((res)=>{
      this.list=res;

    });

  };
  post() {
this.rou.navigate(['post']);
  }

  deleteid(id:string) {
this.rou.navigate(['delete',id]);
    }
    update(id:string) {
  this.rou.navigate(['update',id]);
    }
    getById(id:string) {
    this.rou.navigate(['getById',id]);
    }

}
<div class="container">
    <div class="row justify-content-center">
        <div class="col-12 col-md-8 col-lg-5 text-center">
            <h1 class="h1">𝓣𝓸 𝓓𝓸 𝓛𝓲𝓼𝓽</h1>
        </div>
    </div>

    <div class="row justify-content-center mb-3">
        <div class="col-12 col-md-8 col-lg-5 text-center">
            <button (click)="post()" class="btn btn-secondary w-100">➤ Add Task</button>
        </div>
    </div>

    <div class="row justify-content-center">
        <div class="col-12 col-md-8 col-lg-5">
            <div class="card-container">
                <div *ngFor="let l of list" class="card mb-3">
                    <div class="card-body">
                        <p class="card-text">{{ l.description }}</p>
                        <div class="text-end">
                            <p class="card-text">{{ l.date }}</p>
                        </div>
                    </div>
                    <div class="card-footer d-flex justify-content-between">
                        <button (click)="getById(l._id)" class="btn btn-primary btn-sm">Show</button>
                        <button (click)="update(l._id)" class="btn btn-warning btn-sm">Update</button>
                        <button (click)="deleteid(l._id)" class="btn btn-danger btn-sm">Delete</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
