import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { List } from '../list';
import { ListService } from '../list.service';

@Component({
  selector: 'app-get-all',
  templateUrl: './get-all.component.html',
  styleUrls: ['./get-all.component.css']
})
export class GetAllComponent implements OnInit{



  constructor (private rou:Router,private ser:ListService) {}

list:List[]=[];

ngOnInit(): void {
this.getAll();
  }

  getAll() {
    this.ser.getAll().subscribe((res)=>{
      this.list=res;

    });

  };
  post() {
this.rou.navigate(['post']);
  }

  deleteid(id:string) {
this.rou.navigate(['delete',id]);
    }
    update(id:string) {
  this.rou.navigate(['update',id]);
    }
    getById(id:string) {
    this.rou.navigate(['getById',id]);
    }

}
<div class="container">
    <div class="header text-center">
        <div class="h1">𝓣𝓸 𝓓𝓸 𝓛𝓲𝓼𝓽</div>
    </div>

    <div class="add-button text-center">
        <button (click)="post()" class="btn btn-secondary"> ➤ Add Task </button>
    </div>

    <div class="card-container">
        <div *ngFor="let l of list" class="card">
            <div class="card-body">
                <p class="card-text">{{ l.description }}</p>
                <div class="text-end">
                    <p class="card-text">{{ l.date }}</p>
                </div>
            </div>
            <div class="card-footer d-flex flex-wrap justify-content-between">
                <button (click)="getById(l._id)" class="btn btn-list flex-grow-1 mb-2 me-2">Show</button>
                <button (click)="update(l._id)" class="btn btn-update flex-grow-1 mb-2 me-2">Update</button>
                <button (click)="deleteid(l._id)" class="btn btn-delete flex-grow-1 mb-2">Delete</button>
            </div>
        </div>
    </div>
</div>
